mapper.select:
select member_id, resume_existence, member_name, member_email, question_count, total_comment_count from view_member_list where resume_existence = false
and member_name like concat('%',#{menteeKeyword}, '%')
limit #{startRow}, #{rowCount}

mapper.delete:
delete from
tbl_member
where resume_existence = false and member_id = #{memberId}

mapper.select:
select member_id, resume_existence, member_name, member_email, question_count, total_comment_count from view_member_list where resume_existence = true
and member_name like concat('%',#{mentorKeyword}, '%')
limit #{startRow}, #{rowCount}

mapper.delete:
delete from
tbl_member
where resume_existence = true and member_id = #{memberId}

mapper.select:
select answer_id, category_name, member_name, answer_title, answer_content, comment_count, like_count, answer_regdate, question_id from view_mentor_reply_list where resume_existence = true
and answer_title like concat('%',#{mentorReplyKeyword}, '%')
limit #{startRow}, #{rowCount}

select question_id, category_name, member_name, question_title, question_content, answer_count, comment_count, question_regdate from view_mentee_question_list where resume_existence = false
and question_title like concat('%',#{menteeQuestionKeyword}, '%')
limit #{startRow}, #{rowCount}
